

import unittest


def pattern_count(text, pattern):
    count = 0
    for i in range(len(text)-len(pattern)+1):
        if text[i:i+len(pattern)] == pattern:
            count = count+1
    return count


def frequency_map(text, k):
    freq = {}
    n = len(text)
    for i in range(n-k+1):
        pattern = text[i:i+k]
        if pattern in freq.keys():
            freq[pattern] += 1
        else:
            freq[pattern] = 1
    return freq


def frequent_words(text, k):
    words = []
    freq = frequency_map(text, k)
    m = max(freq.values())
    for key in freq:
        if freq[key] == m:
            words.append(key)
    return words


class TestPatternCount(unittest.TestCase):

    def test_pattern_count_0(self):
        text = ''
        pattern = 'AGCCTTTAG'
        output = 294
        self.assertEqual(pattern_count(text, pattern), output)

    def test_pattern_count_1(self):
        text = ''
        pattern = 'GCCATTTGC'
        output = 26
        self.assertEqual(pattern_count(text, pattern), output)

    def test_frequent_words_0(self):
        text = 'ACGTTGCATGTCGCATGATGCATGAGAGCT'
        k = 4
        output = 'CATG GCAT'
        self.assertCountEqual(' '.join(frequent_words(text, k)), output)

    def test_frequent_words_1(self):
        text = ''
        k = 12
        output = 'GGAGAAGCGAGA GCGTGGCGTGCG CGTGGCGTGCGG CGGCGGGAGATT GGCGGGAGATTC GCGTGGAGGCGT CGTGGAGGCGTG GCGTGCGGCGGG CGTGCGGCGGGA GTGCGGCGGGAG TGCGGCGGGAGA CGGGAGATTCAA GGGAGATTCAAG GGAGATTCAAGC'
        self.assertCountEqual(' '.join(frequent_words(text, k)), output)

    def test_frequent_words_2(self):
        text = ''
        k = 13
        output = 'TGCTTATGTACAA GCTTATGTACAAC'
        self.assertCountEqual(' '.join(frequent_words(text, k)), output)


if __name__ == '__main__':
    unittest.main()
