

import unittest
import os


def pattern_matching(pattern, genome):
    positions = []
    for i in range(len(genome)-len(pattern)+1):
        if genome[i:i+len(pattern)] == pattern:
            positions.append(i)
    return positions


class TestPatternMatching(unittest.TestCase):

    def test_pattern_matching_0(self):
        pattern = 'ATAT'
        genome = 'GATATATGCATATACTT'
        output = [1, 3, 9]
        self.assertEqual(pattern_matching(pattern, genome), output)

